select count(1) from feedback where is_delete = 0 and coach_id > 0
  and feedback_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and feedback_time <= #{endTime,jdbcType=TIMESTAMP}

select count(1) from feedback where is_delete = 0 and create_by > 0
  and feedback_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and feedback_time <= #{endTime,jdbcType=TIMESTAMP}

select count(1) from task where is_delete = 0 and review_status = 3 and type = 2 and status in (2,3)
  and create_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and create_time <= #{endTime,jdbcType=TIMESTAMP}

select count(1) from task where is_delete = 0 and review_status = 3 and type = 1 and status in (2,3)
  and create_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and create_time <= #{endTime,jdbcType=TIMESTAMP}

select count(1) from `feedback` where is_delete = 0 and `type` = #{type}
  and feedback_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and feedback_time <= #{endTime,jdbcType=TIMESTAMP}

select count(1) from `feedback` where is_delete = 0
  and feedback_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and feedback_time <= #{endTime,jdbcType=TIMESTAMP}

select sum(tu.task_hours) from task_user tu
left join task t on tu.task_id = t.id
where t.is_delete = 0 and t.review_status = 3 and t.status in (2,3)
  and tu.begin_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and tu.begin_time <= #{endTime,jdbcType=TIMESTAMP}

select t.project_id,count(t.id) task_num,p.name project_name from task t
left join project p on t.project_id = p.id
where t.is_delete = 0 and t.review_status = 3 and t.status in (2,3)
  and t.create_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and t.create_time <= #{endTime,jdbcType=TIMESTAMP} 
group by t.project_id

select count(id) from `user_leave` where review_status = 3
  and create_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and create_time <= #{endTime,jdbcType=TIMESTAMP}

select sum(hours) from `user_leave` where review_status = 3
  and begin_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and begin_time <= #{endTime,jdbcType=TIMESTAMP}

select concat(MONTH(begin_time),'-',COUNT(id),'-',SUM(hours)) from `user_leave` where review_status = 3
  and begin_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and begin_time <= #{endTime,jdbcType=TIMESTAMP} 
group by MONTH(begin_time)

select count(id) task_num,sum(task_hours) task_time FROM `task_user` WHERE user_id = #{userId} and status in (2,3)
  and begin_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and begin_time <= #{endTime,jdbcType=TIMESTAMP}

select count(id) vacation_count,sum(hours) vacation_time from `user_leave` where review_status = 3 and user_id = #{userId}
  and create_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and create_time <= #{endTime,jdbcType=TIMESTAMP}

select count(tu.id) from  `task_user` tu
left join `task` t on tu.task_id = t.id
where tu.user_id = #{userId} and tu.status in (2,3) and t.type = 2
  and tu.begin_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and tu.begin_time <= #{endTime,jdbcType=TIMESTAMP}

select tu.task_id from task_user tu
left join task t on tu.task_id = t.id
where t.is_delete = 0 and t.review_status = 3 and t.type = 2 and t.status in (2,3) and tu.user_id = #{userId}
  and tu.begin_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and tu.begin_time <= #{endTime,jdbcType=TIMESTAMP} 
group by tu.task_id
order by sum(tu.task_hours) desc limit 1

select tu.task_id from task_user tu
left join task t on tu.task_id = t.id
where t.is_delete = 0 and t.review_status = 3 and t.type = 2 and t.status in (2,3) and tu.user_id = #{userId}
  and tu.begin_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and tu.begin_time <= #{endTime,jdbcType=TIMESTAMP} 
group by tu.task_id
order by sum(tu.task_hours) asc limit 1

select ifnull(count(id),0) from `task` where is_delete = 0 and review_status = 3 and status in (2,3) and priority = #{priority}
  and create_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and create_time <= #{endTime,jdbcType=TIMESTAMP}

select concat(MONTH(feedback_time),'-',ifnull(count(id),0)) from `feedback` where is_delete = 0
  and feedback_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and feedback_time <= #{endTime,jdbcType=TIMESTAMP} 
group by MONTH(feedback_time)

select concat(ifnull(MONTH(complete_time),12),'-',ifnull(COUNT(id),0)) from `task` where is_delete = 0 and review_status = 3 and status in (2,3)
  and create_time >= #{beginTime,jdbcType=TIMESTAMP} 
  and create_time <= #{endTime,jdbcType=TIMESTAMP} 
group by MONTH(complete_time)

