mapper.select:
select t1.*,t2.`name` as projectName,t3.`name` as stageName from task t1
left join project t2 on t1.project_id = t2.id
left JOIN stage t3 on t1.stage_id = t3.id
where t1.id = #{taskId} and t1.is_delete = 0

    and t1.stage_id = #{stage}


    and t1.status = #{status}

select DISTINCT t1.id as ex_id, t1.*,t3.name as projectName,t3.image_url as projectImage, t2.user_id,t4.`name` as userName, t4.avatar_url from task t1
join task_user t2 on t1.id = t2.task_id
left join project t3 on t1.project_id = t3.id
   left join `user` t4 on t1.create_by = t4.id
where t1.`status` = #{status} and t1.review_status = #{reviewStatus}
and t2.`status`=#{taskUserStatus}
and t1.is_delete = 0
and t2.user_id = #{userId}
order by t1.create_time desc

select DISTINCT(t1.id) as dist, t1.*, t4.`name` as projectName,t4.image_url as projectImage, t5.`name` as stageName,t6.`name` as userName,t6.avatar_url from
task t1
left join task_user t2 on t1.id =t2.task_id
left join task_tag t3 on t1.id = t3.task_id
left join project t4 on t1.project_id = t4.id
left JOIN stage t5 on t1.stage_id = t5.id
left join `user` t6 on t1.create_by = t6.id
left join  department t7 on t6.department_id = t7.id

    t1.review_status = 3 and t1.is_delete = 0 and t7.parent_id = #{departmentId}

        and t1.id = #{taskId}


        and t1.project_id = #{projectId}


        and t1.type = #{type}


        and t2.user_id = #{userId}


        and t1.stage_id in

            #{item}



        and t1.priority = #{priority}


        and t3.tag_id in

            #{item}



        and t1.`status` = 1


        and t1.`status` = 0


        and t1.`status` in (2,3)


        and t1.create_time >= #{beginTime}


        and t1.create_time <= #{endTime}


        and t1.create_by = #{createBy}




       order by t1.priority DESC


       order by t1.end_time DESC


       order by t1.complete_time DESC


       order by t1.create_time DESC


       order by t1.end_time ASC

select DISTINCT(t1.id) as dist, t1.*, t4.`name` as projectName, t4.image_url as projectImage, t5.`name` as stageName,t6.`name` as userName,t6.avatar_url from task t1 left join task_user t2 on t1.id =t2.task_id left join task_tag t3 on t1.id = t3.task_id left join project t4 on t1.project_id = t4.id left JOIN stage t5 on t1.stage_id = t5.id left join `user` t6 on t1.create_by = t6.id left join department t7 on t6.department_id = t7.id where t1.review_status = 3 and t1.is_delete = 0 and t1.`type` = 2 and t1.stage_id = #{stageId} and t7.parent_id =#{departmentId}
and t1.end_time <= #{time}
order by t1.sort ASC

select DISTINCT(t1.id) as dist, t1.*, t4.`name` as projectName, t4.image_url as projectImage, t5.`name` as stageName,t6.`name` as userName,t6.avatar_url from task t1 left join task_user t2 on t1.id =t2.task_id left join task_tag t3 on t1.id = t3.task_id left join project t4 on t1.project_id = t4.id left JOIN stage t5 on t1.stage_id = t5.id left join `user` t6 on t1.create_by = t6.id left join department t7 on t6.department_id = t7.id where t1.review_status = 3 and t1.is_delete = 0 and t1.status !=3 and t1.`type` = 2 and t1.stage_id = #{stageId} and t7.parent_id =#{departmentId}
and t1.end_time <= #{time}
order by t1.sort ASC

select count(*) from task_user  where status =1
  and end_time<now() 
and task_id = #{id}
