mapper.select:
SELECT (@rowid := @rowid+1) AS id,integral,name,user_id FROM( SELECT sum(i.integral) as integral, u.name, i.user_id FROM user_integral i,`user` u
i.user_id = u.id
AND i.create_time >= #{startTime}
AND i.create_time <#{endTime}
GROUP BY i.user_id ORDER BY integral DESC)as a, (SELECT (@rowid:=0))as b

SELECT  IFNULL(sum(integral),0) FROM user_integral

  user_id = #{id,jdbcType=BIGINT}
    AND create_time > #{startTime,jdbcType=TIMESTAMP}
              AND create_time < #{endTime,jdbcType=TIMESTAMP}

SELECT i.integral, u.name, i.origin, i.create_time, i.description
FROM user_integral i,`user` u

  i.user_id = #{id,jdbcType=BIGINT}
  AND i.user_id = u.id

    AND i.create_time >= #{startTime}


    AND i.create_time <#{endTime}


ORDER BY i.create_time DESC
