SELECT ucp.*,u.name user_name FROM `user_check_people` ucp
LEFT JOIN `user` u ON u.id = ucp.check_user_id
WHERE ucp.`user_id` = #{userId}

SELECT ucp.*,u.name user_name FROM `user_check_people` ucp
LEFT JOIN `user` u ON u.id = ucp.user_id
