SELECT u.* FROM task_user tu LEFT JOIN user u
ON tu.`user_id` = u.`id`
WHERE tu.`task_id` = #{taskId}

SELECT * FROM `user` WHERE `is_delete` = 0 AND `status` = 0

    AND `job_role`= #{jobRole}


    AND `id`= #{userId}

SELECT u.* FROM `task_user` tu
LEFT JOIN `user` u ON u.`id` = tu.`user_id`
